	   public boolean onOptionsItemSelected(MenuItem item) {
			// TODO Auto-generated method stub
		
			
			
			switch(item.getItemId()){	
			case R.id.aboutUs:
				  
				Intent i2=new Intent("android.intent.action.ABOUTUS");
				startActivity(i2);
					break;
					
				case R.id.share:
					//create the send intent  
					Intent shareIntent =   
					 new Intent(android.content.Intent.ACTION_SEND);  
					  
					//set the type  
					shareIntent.setType("text/plain");  
					  
					//add a subject  
					shareIntent.putExtra(android.content.Intent.EXTRA_SUBJECT,   
					 "Take a look at Game of thrones encyclopedia: ");  
					  
					//build the body of the message to be shared  
					String shareMessage = " https://play.google.com/store/apps/details?id=com.appengers.database1";  
					  
					//add the message  
					shareIntent.putExtra(android.content.Intent.EXTRA_TEXT,   
					 shareMessage);  
					  
					//start the chooser for sharing  
					startActivity(Intent.createChooser(shareIntent,   
					 "Share the app via"));  
					
					break;
					
					
				case R.id.information:
					Intent i=new Intent("android.intent.action.INFORMATION5");
					startActivity(i);
					break;
					
				case R.id.quotes:
					Intent myIntent = new Intent(MainActivity.this, Start.class);
					MainActivity.this.startActivity(myIntent);
				//	System.exit(0);
					break;
					
				case R.id.exit:
					System.exit(0);
					break;
		}
		return false;
		}
	   
	   
